    /**
     * Método utilizado para limpiar los campos del formulario.
     */
    private void LimpiarFormulario(){
        tfNombre.setText(null);
        tfCedula.setText(null);
        tfTelefono.setText(null);
        cbTipo.setSelectedItem(null);
    }
    
    /**
     * Método utilizado para limpiar los campos de la etiqueta.
     */
    public void LimpiarLabel(){
        tfNombre.setText(null);
        tfCedula.setText(null);
        tfTelefono.setText(null);
        cbTipo.setSelectedItem(null);
    }
    
    private void cbLimpiarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_cbLimpiarActionPerformed
        LimpiarFormulario();
    }//GEN-LAST:event_cbLimpiarActionPerformed

    /**
     * Método que valida si los datos ingresados en el formulario son del tipo correcto.
     * @return false si el formulario está incompleto.
     * @return true si el formulario se llenó correctamente.
     */
    private boolean formularioValido() throws Exception{
        if(tfNombre.getText().equals("") ||
            tfCedula.getText().equals("") ||
            tfTelefono.getText().equals("") ||    
            cbTipo.getSelectedItem().equals("")){  
            JOptionPane.showMessageDialog(this, "Formulario incompleto!", "Ingreso", JOptionPane.ERROR_MESSAGE);
            return false;
        }
    return true;
    }
    
    private void cbcRegistrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_cbcRegistrarActionPerformed
        try {
            if(formularioValido()){
                String nombre        = tfNombre.getText();
                String cedula         = tfCedula.getText();
                String telefono       = tfTelefono.getText();
                String tipo           = (String)cbTipo.getSelectedItem();

                ConexionBD c = new ConexionBD();
                try{
                    c.conectar();
                    operador o = new operador(nombre,cedula,telefono,tipo);
                    if(c.ingresarOperador(o)){
                        JOptionPane.showMessageDialog(this, "Se ingresó correctamente!", "Ingreso", JOptionPane.INFORMATION_MESSAGE);
                        System.out.println("Ingreso exitoso!");
                    }else{
                        System.out.println("Ingreso Fallido!");
                        JOptionPane.showMessageDialog(this, "Ocurrió un error en el registro!", "Ingreso", JOptionPane.ERROR_MESSAGE);
                    }
                    c.desconectar();
                }catch(Exception e){
                    System.out.println("Ocurrio un problema en el ingreso!");
                }
                LimpiarFormulario();
                LimpiarLabel();
            }
        }catch (Exception ex) {
            
        }
    }//GEN-LAST:event_cbcRegistrarActionPerformed


